mapper.select:
SELECT H.CO_DIV , H.TYPE , H.IDX , H.TITLE , H.BOARD_DIV , H.JOIN_STATUS , CAST(H.CONTENT AS CHAR(10000) CHARACTER SET UTF8) AS CONTENT , H.BK_DAY , H.BK_TIME , H.BK_PERSON , H.BK_FIRST_PHONE , CAST(AES_DECRYPT(UNHEX(H.BK_MID_PHONE),'JBOG') AS CHAR) , H.BK_LAST_PHONE , H.LINK
, IFNULL(NULLIF(U.MS_NAME, ''), '') AS WRITER_NAME
, IFNULL(NULLIF(U.USER_NAME, ''), '관리자') AS WRITER_NAME
, DATE_FORMAT(H.INPUT_DATETIME, '%Y.%m.%d') AS INPUT_DATETIME , IFNULL(NULLIF(D.FILE_NAME1, ''), '') AS FILE_NAME1 , IFNULL(NULLIF(D.FILE_PATH1, ''), '') AS FILE_PATH1 , IFNULL(NULLIF(D.FILE_NAME2, ''), '') AS FILE_NAME2 , IFNULL(NULLIF(D.FILE_PATH2, ''), '') AS FILE_PATH2 , IFNULL(NULLIF(D.FILE_NAME3, ''), '') AS FILE_NAME3 , IFNULL(NULLIF(D.FILE_PATH3, ''), '') AS FILE_PATH3 , IFNULL(NULLIF(D.ATTACH_CNT, 0), 0) AS ATTACH_CNT , H.VIEW_CNT , H.DEL_DIV FROM HP_BOARD H
LEFT OUTER JOIN MS_MAININFO U ON H.INPUT_STAFF = U.MS_NUM
LEFT OUTER JOIN USER_INFO U ON H.INPUT_STAFF = U.USER_STAFF
LEFT OUTER JOIN ( SELECT CO_DIV , TYPE , IDX , IFNULL(NULLIF(FILE_NAME1, ''), '') AS FILE_NAME1 , IFNULL(NULLIF(FILE_PATH1, ''), '') AS FILE_PATH1 , IFNULL(NULLIF(FILE_NAME2, ''), '') AS FILE_NAME2 , IFNULL(NULLIF(FILE_PATH2, ''), '') AS FILE_PATH2 , IFNULL(NULLIF(FILE_NAME3, ''), '') AS FILE_NAME3 , IFNULL(NULLIF(FILE_PATH3, ''), '') AS FILE_PATH3 , IF(IFNULL(NULLIF(FILE_NAME2, ''), '') = '', 0, 1) + IF(IFNULL(NULLIF(FILE_NAME3, ''), '') = '', 0, 1) AS ATTACH_CNT FROM HP_BOARD_ATTACH ) D ON H.CO_DIV = D.CO_DIV AND H.TYPE = D.TYPE AND H.IDX = D.IDX WHERE H.CO_DIV = #{coDiv} AND H.TYPE = #{sType}
AND H.DEL_DIV = '0'
AND H.BOARD_DIV = #{boardDiv}
AND H.BK_DAY BETWEEN #{stDate} AND #{edDate}
AND H.TITLE LIKE CONCAT('%',#{searchText},'%')
AND CAST(H.CONTENT AS CHAR(10000) CHARACTER SET UTF8) LIKE CONCAT('%',#{searchText},'%')
AND U.USER_NAME = #{searchText}
ORDER BY H.IDX DESC LIMIT ${startCnt}, ${pageCnt}

SELECT COUNT(*) TOTAL_CNT FROM HP_BOARD H
LEFT OUTER JOIN MS_MAININFO U ON H.INPUT_STAFF = U.MS_NUM
LEFT OUTER JOIN USER_INFO U ON H.INPUT_STAFF = U.USER_STAFF
LEFT OUTER JOIN ( SELECT CO_DIV , TYPE , IDX , IFNULL(NULLIF(FILE_NAME1, ''), '') AS FILE_NAME1 , IFNULL(NULLIF(FILE_PATH1, ''), '') AS FILE_PATH1 , IF(IFNULL(NULLIF(FILE_NAME2, ''), '') = '', 0, 1) + IF(IFNULL(NULLIF(FILE_NAME3, ''), '') = '', 0, 1) AS ATTACH_CNT FROM HP_BOARD_ATTACH ) D ON H.CO_DIV = D.CO_DIV AND H.TYPE = D.TYPE AND H.IDX = D.IDX WHERE H.CO_DIV = #{coDiv}
AND H.DEL_DIV = '0'
AND H.TYPE = #{type}
AND H.BOARD_DIV = #{boardDiv}
AND H.BK_DAY BETWEEN #{stDate} AND #{edDate}
AND H.TITLE LIKE CONCAT('%',#{searchText},'%')
AND CAST(H.CONTENT AS CHAR(10000) CHARACTER SET UTF8) LIKE CONCAT('%',#{searchText},'%')
AND U.USER_NAME = CONCAT('%',#{searchText},'%')

mapper.update:
UPDATE HP_BOARD H
SET VIEW_CNT = IFNULL(NULLIF(H.VIEW_CNT, 0), 0) + 1
WHERE CO_DIV = #{coDiv}
  AND TYPE = #{sType}
  AND IDX = #{sIdx}

mapper.select:
SELECT IFNULL(NULLIF(MAX(IDX), 0), 0) + 1 AS IDX
FROM HP_BOARD
WHERE CO_DIV = #{coDiv}
  AND TYPE = #{sType}

mapper.update:
UPDATE HP_BOARD
SET TITLE           = #{title}
  , CONTENT         = #{content}
  , BOARD_DIV       = #{boardDiv}
  , JOIN_STATUS     = #{joinStatus}
  , BK_DAY          = #{bkDay}
  , BK_TIME         = #{bkTime}
  , BK_PERSON       = #{bkPerson}
  , BK_FIRST_PHONE  = #{phone1}
  , BK_MID_PHONE    = HEX(AES_ENCRYPT(#{phone2}, 'JBOG'))
  , BK_LAST_PHONE   = #{phone3}
  , LINK            = #{link}
  , UPDATE_STAFF    = #{staff}
  , UPDATE_DATETIME = NOW()
  , UPDATE_IP       = #{ipAddr}
WHERE CO_DIV = #{coDiv}
  AND TYPE = #{sType}
  AND IDX = #{idx}

UPDATE HP_BOARD_ATTACH
SET CO_DIV = CO_DIV

  , FILE_NAME1 = #{fileName1}
  , ORIGIN_FILE_NAME1 = #{originFileName1}
  , FILE_PATH1 = #{filePath1}


  , FILE_NAME2 = #{fileName2}
  , ORIGIN_FILE_NAME2 = #{originFileName2}
  , FILE_PATH2 = #{filePath2}


  , FILE_NAME3 = #{fileName3}
  , ORIGIN_FILE_NAME3 = #{originFileName3}
  , FILE_PATH3 = #{filePath3}

WHERE CO_DIV = #{coDiv}
AND TYPE = #{sType}
AND IDX = #{idx}

UPDATE HP_BOARD
SET DEL_DIV = #{sDiv}
WHERE CO_DIV = #{coDiv}
  AND TYPE = #{sType}
  AND IDX = #{sIdx}

mapper.select:
SELECT H.CO_DIV , H.TYPE , H.IDX , 0 AS SEQ , H.TITLE , CAST(H.CONTENT AS CHAR(10000) CHARACTER SET UTF8) AS CONTENT , H.BOARD_DIV , H.JOIN_STATUS , IFNULL(NULLIF(U.MS_NAME, ''), '') AS WRITER_NAME , DATE_FORMAT(H.INPUT_DATETIME, '%Y.%m.%d') AS INPUT_DATETIME , H.VIEW_CNT FROM HP_BOARD H LEFT OUTER JOIN MS_MAININFO U ON H.INPUT_STAFF = U.MS_NUM WHERE H.CO_DIV = #{coDiv} AND H.TYPE = #{sType} AND H.DEL_DIV = '0' AND H.BK_DAY BETWEEN #{stDate} AND #{edDate}
AND H.TITLE LIKE CONCAT('%',#{searchText},'%')
AND CAST(H.CONTENT AS CHAR(10000) CHARACTER SET UTF8) LIKE CONCAT('%',#{searchText},'%')
AND U.MS_NAME = CONCAT('%',#{searchText},'%')
LEFT OUTER JOIN USER_INFO U ON H.INPUT_STAFF = U.USER_STAFF
ORDER BY IDX DESC, SEQ LIMIT ${startCnt}, ${pageCnt}

mapper.update:
UPDATE HP_BOARD_REPLY SET DEL_DIV = '1'
WHERE CO_DIV = #{coDiv}
  AND TYPE = #{sType}
  AND IDX = #{sIdx}
  AND SEQ = #{sSeq}
